import re

line = ''

pattern = r"(?=([a-z]{1})[A-Z]+([a-z]{1}))"
result = re.findall(pattern, line)

print(result)

def circled(line):
    w = []
    lowers = [y for y in range(len(line)) if(line[y].islower())]
    for i in range(0, len(lowers) - 1):
        if((lowers[i + 1] - lowers[i]) > 1):
            tup = (line[lowers[i]], line[lowers[i + 1]])
            w.append(tup)
    return w

print(circled(line))
