import re

line = ''

pattern = r"(?=([A-Z]{1})([a-z]{2})+([A-Z]{1}))"
result = re.findall(pattern, line)

print(result)

def circled(line):
    w = []
    uppers = [y for y in range(len(line)) if(line[y].isupper())]
    for i in range(0, len(uppers) - 1):
        if((uppers[i+1] - uppers[i]) == 3):
            tup = (line[uppers[i]], line[uppers[i + 1]])
            w.append(tup)
    return w

print(circled(line))
